<script lang="ts" setup>
import {
  defineProps,
  defineEmits,
  getCurrentInstance,
  defineComponent,
  ref,
  nextTick,
  onMounted,
  onUpdated,
  reactive,
  toRefs,
  Ref, ComponentInternalInstance
} from "vue"
import {Home} from "./HomeBase"
import {
  Location,
  UserFilled,
  Goods,
  HomeFilled,
  ChatDotSquare,
  ShoppingCart,
  ScaleToOriginal,
  Setting
} from '@element-plus/icons-vue'
import {Router} from "vue-router";


// 获取当前实例，vue2 = this
// let ins = getCurrentInstance();

let home : Home = new Home();
// 获取数据
let obj = home.obj;
// @ts-ignore
let router : Router = getCurrentInstance().proxy.$router;
let objs : object = {
  "1-1": ["首页", ""],
  "2-1": ["病患就诊", "Gh"],
  "2-2": ["药品管理", "drug"],
  "2-3": ["住院管理","inH"],

  "5-1": ["药品采购","PR"],
  "5-2": ["供应商管理","Supplier"],

  "6-1": ["用户管理",""],
  "6-2": ["岗位管理",""]
};
let select = (index : string, path: string[], item: string) => {
  // @ts-ignore
  const TabeleName: string[] =  objs[index];

  if ( (TabeleName[0] != null)
      && TabeleName[0].trim().length > 0
     )
  {
    home.AddTab(TabeleName[0], TabeleName[1], router);
  }
};

const Remove = (Name: string) => home.RemoveTab(Name);

const Showclick = function ( Pane : any, ev: string)
{
  let CurrentCLick: string = Pane.props.label;
  let TabaName : string[] = [];
  let key: string[] = Object.keys(objs);
  let values: string[][] = Object.values(objs);

  for (let i = 0; i < key.length; i++) {
    if (values[i][0] === CurrentCLick) {
     TabaName = values[i];
    }
  }
  home.AddTab(TabaName[0], TabaName[1], router);
}


</script>

